import React, {useEffect, useState} from 'react';
import './App.css';
import {Form} from '@bpmn-io/form-js';
import "@bpmn-io/form-js/dist/assets/form-js.css";


function RenderForm(props: any) {
    const [form, setForm] = useState<Form>()

    useEffect(() => {
        if (props.reset || !props.schema) {
            if (form) {
                form.clear();
                form.reset();
            }
            return;
        }
        let tmpForm = new Form({
            container: document.querySelector('#form')
        })
        // @ts-ignore
        tmpForm.on("submit", ({data, errors}) => {
            console.log(data, errors);
        });
        tmpForm.clear();
        tmpForm
            .importSchema(props.schema)
            .catch((error) => console.error("cannot import form schema", error));
        setForm(tmpForm)
    }, [form, props.schema, props.reset]);

    return (
        <div id="form"/>
    );
}

export default RenderForm;
